    <script>
        const container = document.querySelector('.accordion-container');
        const header = document.querySelector('.accordion-header');
        const content = document.querySelector('.accordion-content');
        const particlesWrapper = document.querySelector('.particles-wrapper');

        // --- 1. 简化折叠逻辑 - 去掉动画 ---
        header.addEventListener('click', function() {
            container.classList.toggle('expanded');
        });

        // --- 2. 主题切换与粒子效果 ---
        const themeButtons = document.querySelectorAll('.theme-controls button');
        const themes = ['stars', 'snow', 'bubbles'];
        let particleInterval = null;

        function clearParticles() {
            if (particleInterval) clearInterval(particleInterval);
            particleInterval = null;
            if(particlesWrapper) particlesWrapper.innerHTML = '';
        }

        function createSnowflakes() {
            particleInterval = setInterval(() => {
                const snowflake = document.createElement('div');
                snowflake.className = 'snowflake';
                const size = Math.random() * 4 + 2;
                const duration = Math.random() * 5 + 7;
                snowflake.style.width = `${size}px`;
                snowflake.style.height = `${size}px`;
                snowflake.style.left = `${Math.random() * 100}%`;
                snowflake.style.animationDuration = `${duration}s`;
                snowflake.style.animationDelay = `${Math.random() * 5}s`;
                snowflake.style.opacity = Math.random() * 0.7 + 0.3;
                particlesWrapper.appendChild(snowflake);
                setTimeout(() => snowflake.remove(), (duration + 5) * 1000);
            }, 150);
        }

        function createBubbles() {
            particleInterval = setInterval(() => {
                const bubble = document.createElement('div');
                bubble.className = 'bubble';
                const size = Math.random() * 80 + 20;
                const duration = Math.random() * 10 + 8;
                const hue = Math.random() * 360;

                bubble.style.width = `${size}px`;
                bubble.style.height = `${size}px`;
                bubble.style.top = `${Math.random() * 90}%`;
                bubble.style.animationDuration = `${duration}s`;
                bubble.style.animationDelay = `${Math.random() * 4}s`;
                bubble.style.background = `radial-gradient(circle at 30% 30%, hsla(${hue}, 100%, 75%, 0.8), hsla(${hue}, 100%, 50%, 0.2))`;

                if (Math.random() > 0.5) {
                    bubble.classList.add('from-left');
                    bubble.style.left = `-${size + 20}px`;
                } else {
                    bubble.classList.add('from-right');
                    bubble.style.right = `-${size + 20}px`;
                }

                particlesWrapper.appendChild(bubble);
                setTimeout(() => bubble.remove(), (duration + 5) * 1000);
            }, 300);
        }

        themeButtons.forEach(button => {
            button.addEventListener('click', function(event) {
                const selectedTheme = event.target.dataset.theme;
                clearParticles();
                themes.forEach(theme => container.classList.remove('theme-' + theme));
                container.classList.add('theme-' + selectedTheme);
                if (selectedTheme === 'snow') createSnowflakes();
                else if (selectedTheme === 'bubbles') createBubbles();
                themeButtons.forEach(btn => btn.classList.remove('active'));
                event.target.classList.add('active');
            });
        });
    </script>

