    <script>
      /**
       * 与SillyTavern环境通信，触发快速回复。
       * @param {string} text - 要发送的文本。
       */
      const triggerQuickReply = (text) => {
        // 检查文本是否有效，避免发送空内容
        if (!text || text.trim() === "") {
          console.warn("Attempted to send an empty message.");
          return;
        }

        // 检查SillyTavern环境并发送命令
        if (typeof triggerSlash === "function") {
          triggerSlash(`/send ${text}|/trigger`);
        } else {
          console.log("SillyTavern environment not detected. Mock send:", text);
        }
      };

      /**
       * 处理选项列表的点击事件。
       * @param {MouseEvent} event - 点击事件对象。
       */
      const handleOptionClick = (event) => {
        // 使用 event.target.closest() 查找被点击的 .option-link 元素
        const link = event.target.closest(".option-link");
        if (!link) {
          return; // 如果点击的不是 .option-link 或其子元素，则不执行任何操作
        }

        // 使用 .dataset 属性获取 data- 属性，这是更现代的写法
        const fullText = link.dataset.optionText;

        // 增加对 fullText 是否存在的检查，防止属性缺失导致错误
        if (typeof fullText !== "string") {
          console.error(
            "Clicked link is missing 'data-option-text' attribute.",
            link
          );
          return;
        }

        // 更稳健地移除首尾的方括号，仅当它们存在时才移除
        const optionText =
          fullText.startsWith("[") && fullText.endsWith("]")
            ? fullText.slice(1, -1)
            : fullText;

        console.info("触发文本", { fullText, optionText });

        // 触发快速回复
        triggerQuickReply(optionText);
      };

      /**
       * 初始化事件监听器。
       */
      const initializeEventListeners = () => {
        const optionList = document.getElementById("izumi-option-list");

        // 确认元素存在后再添加事件监听，避免页面上没有该元素时报错
        if (optionList) {
          optionList.addEventListener("click", handleOptionClick);
        } else {
          console.warn(
            "Element with ID 'izumi-option-list' not found. No listener attached."
          );
        }
      };

      // 页面加载完成后执行初始化
      document.addEventListener("DOMContentLoaded", initializeEventListeners);
