    <script>
        (function() {
            const dataSourceElem = document.getElementById('danmaku-data-source');
            const targetContainer = document.getElementById('danmaku-container-final');

            if (!dataSourceElem || !targetContainer) return;

            /* --- 随机背景图列表（替换为幸运星相关图片） --- */
            const bgImages = [                "https://static.zerochan.net/Izumi.Konata.full.887888.jpg",                               "https://www.hiclipart.com/free-transparent-background-png-clipart-xdntq"

            ];
            const randomImg = bgImages[Math.floor(Math.random() * bgImages.length)];
            targetContainer.style.backgroundImage = `url(${randomImg})`;

            // B站常见弹幕颜色（十六进制格式）
            const biliColors = [
                "#FFFFFF", // 白色
                "#FF6B6B", // 红色
                "#FFA500", // 橙色
                "#FFFF00", // 黄色
                "#77DD77", // 绿色
                "#779ECB", // 蓝色
                "#9B59B6", // 紫色
                "#FF69B4", // 粉色
                "#00CED1", // 青色
                "#FFD700"  // 金色
            ];

            const rawDanmus = (dataSourceElem.textContent || dataSourceElem.innerText).trim();
            const danmuLines = rawDanmus.split(/\r?\n/).filter(line => line.trim() !== '');

            const trackStyles = [
                { top: '2%', duration: '20s', delay: '-1s' },
                { top: '15%', duration: '28s', delay: '-11s' },
                { top: '28%', duration: '24s', delay: '-4s' },
                { top: '42%', duration: '33s', delay: '-2s' },
                { top: '56%', duration: '18s', delay: '-16s' },
                { top: '70%', duration: '30s', delay: '-7s' },
                { top: '85%', duration: '22s', delay: '-13s' }
            ];

            function escapeHtml(text) {
                const div = document.createElement('div');
                div.appendChild(document.createTextNode(text));
                return div.innerHTML;
            }

            let allTracksHtml = '';
            danmuLines.forEach((line, index) => {
                const style = trackStyles[index % trackStyles.length];
                // 随机选择B站颜色
                const randomColor = biliColors[Math.floor(Math.random() * biliColors.length)];
                const safeLine = escapeHtml(line.trim());

                allTracksHtml += `
    <div class="danmaku-track-rgb" style="top: ${style.top}; animation-duration: ${style.duration}; animation-delay: ${style.delay};">
        <div class="danmaku-content-multicolor-rgb" style="color: ${randomColor};">${safeLine}</div>
    </div>`;
            });
            targetContainer.innerHTML += allTracksHtml;
        })();
    </script>

</body>
</html> 
